interface Data {
  id: number;
  title: string;
  description: string;
  images: string;
  address: string;
  name: string;
  icon: string;
  message: string;
  day_pass_price: number;
  day_pass_discounts_percentage: {
    "1": {
      value: number;
      message: string;
    };
    "10": {
      value: number;
      message: string;
    };
  };
}
function calculate10DayPrice(dayPassPrice: number, discountPercentage: number): string {
  const complimentaryDays = 2; // Assuming 2 days complimentary
  const totalDays = 10; // Total days including complimentary days
  const discountedDays = totalDays - complimentaryDays;
  const discountedPrice = dayPassPrice * (1 - discountPercentage / 100);
  const finalPrice = discountedPrice * discountedDays;

  const decimalPart = finalPrice % 1; // Extract the decimal part
  return finalPrice.toFixed(2); // Format to two decimal places
}

